        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1391653, 'Clarithromycin', 'Rilpivirine', 'Moderate', 'Coadministration with macrolide antibiotics that are inhibitors of CYP450 3A4 may increase the blood concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if rilpivirine is used with macrolide antibiotics that are inhibitors of CYP450 3A4.', NULL, 'Caution is advised if rilpivirine is used with macrolide antibiotics that are inhibitors of CYP450 3A4. Alternative antibiotics that do not interfere with rilpivirine metabolism, such as azithromycin, should be considered whenever possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
