    ) VALUES (1391654, 'Clarithromycin', 'Rimegepant', 'Moderate', 'Coadministration with strong inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of rimegepant, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration of rimegepant with strong inhibitors of CYP450 3A4 should be avoided.', NULL, 'Coadministration of rimegepant with strong inhibitors of CYP450 3A4 should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
