        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1391659, 'Clarithromycin', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may increase the plasma concentrations of clarithromycin. The mechanism is ritonavir inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic conversion of clarithromycin to 14-hydroxyclarithromycin.', NULL, 'No dosage adjustment of clarithromycin or ritonavir is required during coadministration in patients with normal renal function.', NULL, 'No dosage adjustment of clarithromycin or ritonavir is required during coadministration in patients with normal renal function. However, clarithromycin dosage should be reduced by 50% in patients with moderate renal impairment (CrCl 30 to 60 mL/min) and 75% in patients with more severe renal impairment (CrCl below 30 mL/min).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
