    ) VALUES (1391691, 'Clarithromycin', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort (Hypericum perforatum) may reduce the plasma concentrations and antimicrobial efficacy of clarithromycin. The mechanism is induction of CYP450 3A metabolism by St. John''s wort. Clinical data have not been reported.', NULL, 'If concomitant use is medically necessary, it is recommended to monitor for altered efficacy and safety of clarithromycin.', NULL, 'If concomitant use is medically necessary, it is recommended to monitor for altered efficacy and safety of clarithromycin.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
