INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113583, 'Tildrakizumab', 'Rilonacept', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113716/', '[1] "Product Information. Kineret (anakinra)." Amgen, Thousand Oaks, CA.[2] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113584, 'Tildrakizumab', 'Roflumilast', 'Moderate', 'The concomitant use of roflumilast with immunosuppressive agents has not been adequately studied. Specifically, roflumilast and its active metabolite, roflumilast N-oxide, suppress the release of inflammatory mediators including leukotriene B4, reactive oxygen species, tumor necrosis factor alpha, interferon gamma, and granzyme B. Roflumilast also reduces sputum neutrophils and attenuates influx of neutrophils and eosinophils into the airways.', NULL, 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.', NULL, 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.g., in the treatment of COPD exacerbations) should be avoided if possible. Due to a lack of clinical experience, treatment with roflumilast should not be initiated, or existing treatment should be stopped, in patients receiving immunosuppressive agents and in patients with severe acute infectious diseases, cancers (except basal cell carcinoma), or severe immunological diseases (e.g., HIV infection, multiple sclerosis, lupus erythematosus, progressive multifocal leukoencephalopathy).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113717/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Daxas (roflumilast)." Nycomed Inc, Princeton, NJ.[5] "Product Information. Daliresp (roflumilast)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767363023);
