INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392094, 'Cilostazol', 'Picosulfuric acid', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392095, 'Ciprofloxacin', 'Citric acid', 'Moderate', 'Urinary alkalinizers such as citrates may decrease the solubility of fluoroquinolones in the urine and increase the risk of crystalluria.', 'DDInter', 1767317475, 'Concomitant use should generally be avoided. Patients receiving this combination should be monitored for signs of renal toxicity and crystalluria.', 'Excretion', 'Concomitant use should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392096, 'Cisapride', 'Picosulfuric acid', 'Major', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval.', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Monitoring of baseline and posttreatment serum electrolyte levels is recommended, particularly in the elderly. Patients should be instructed to drink plenty of clear liquids before, during, and after the bowel preparation process.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392097, 'Citalopram', 'Picosulfuric acid', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; hydroxychloroquine; mefloquine), and some narcotic analgesics. The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that can prolong the QT interval, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; mefloquine; halofantrine; lumefantrine), and some narcotic analgesics (methadone; propoxyphene).', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval. Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities. Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process. Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392098, 'Clarithromycin', 'Picosulfuric acid', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate. The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis. Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317475, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic. If concomitant use is required, additional bowel cleansing and colonoscopy may be required. The recommended dosage and duration of treatment with sodium picosulfate should not be exceeded. In addition, patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Others', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
