INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392126, 'Delafloxacin', 'Magnesium oxide', 'Moderate', 'INTERVAL: Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics. Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc. The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317475, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Absorption', 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392127, 'Delavirdine', 'Magnesium oxide', 'Moderate', 'INTERVAL: Aluminum and magnesium containing antacids have been reported to decrease the bioavailability of delavirdine by an average of 41%. The mechanism may be related to decreased absorption of delavirdine. Other antacids may also interact.', 'DDInter', 1767317475, 'Delavirdine administration should be separated by at least 1 hour from one of these preparations.', 'Absorption', 'Delavirdine administration should be separated by at least 1 hour from one of these preparations.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392128, 'Demeclocycline', 'Magnesium oxide', 'Moderate', 'INTERVAL: Administration of a tetracycline with aluminum, calcium, or magnesium salts significantly decreases tetracycline serum concentrations. The proposed mechanism is chelation of tetracycline by the cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract. The interaction has also been reported with parenteral doxycycline and oral antacids.', 'DDInter', 1767317475, 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', 'Absorption', 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392129, 'Desipramine', 'Picosulfuric acid', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; hydroxychloroquine; mefloquine), and some narcotic analgesics. The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that can prolong the QT interval, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; mefloquine; halofantrine; lumefantrine), and some narcotic analgesics (methadone; propoxyphene).', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval. Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities. Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process. Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
