INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392166, 'Elvitegravir', 'Magnesium oxide', 'Moderate', 'INTERVAL: The concomitant use of antacids may significantly decrease the plasma concentrations of elvitegravir. The mechanism of interaction has not been described. In contrast, when the antacid was given 4 hours before or 4 hours after elvitegravir, no significant differences were observed in elvitegravir pharmacokinetics compared to administration of elvitegravir alone.', 'DDInter', 1767317475, 'Antacid-containing preparations should be administered at least 2 hours before or 2 hours after the elvitegravir dose. Alternatively, H2-receptor antagonists or proton pump inhibitors may be considered, with no dosage adjustment needed.', 'Others', 'Antacid-containing preparations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392167, 'Enalapril', 'Picosulfuric acid', 'Moderate', 'Coadministration with agents that affect renal function or perfusion such as diuretics, ACE inhibitors, angiotensin receptor blockers, and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk and/or severity of fluid and electrolyte disturbances associated with the use of bowel cleansing preparations. Fluid and electrolyte disturbances can lead to serious adverse events including cardiac arrhythmias, seizures, and renal impairment.', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment. Baseline and postprocedure labs including serum electrolytes, BUN, and creatinine should be considered. Patients should be advised to drink sufficient quantities of clear fluids before, during, and after bowel cleansing. Hospitalization and intravenous fluid hydration may be appropriate for frail or elderly patients who may be unable to drink an adequate volume of fluid.', 'Synergism', 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392168, 'Encorafenib', 'Picosulfuric acid', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392169, 'Enoxacin', 'Citric acid', 'Moderate', 'Urinary alkalinizers such as citrates may decrease the solubility of fluoroquinolones in the urine and increase the risk of crystalluria.', 'DDInter', 1767317475, 'Concomitant use should generally be avoided. Patients receiving this combination should be monitored for signs of renal toxicity and crystalluria.', 'Excretion', 'Concomitant use should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
