INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392171, 'Enzalutamide', 'Picosulfuric acid', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; hydroxychloroquine; mefloquine), and some narcotic analgesics. The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that can prolong the QT interval, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; mefloquine; halofantrine; lumefantrine), and some narcotic analgesics (methadone; propoxyphene).', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval. Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities. Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process. Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392172, 'Ephedrine', 'Picosulfuric acid', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. Rare cases of generalized tonic-clonic seizures and/or loss of consciousness in association with low serum osmolality and electrolyte abnormalities (e.g., hyponatremia, hypokalemia, hypocalcemia, hypomagnesemia) have been reported with the use of bowel cleansing products in patients with no prior history of seizures. The condition resolved with correction of fluid and electrolyte abnormalities.', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold. Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities. Baseline and postprocedure labs including serum electrolytes, calcium, phosphate, BUN, and creatinine should be considered, particularly in the elderly. Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process. Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.', 'Antagonism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.', NULL, NULL, 'Moderate', 'pharmacodynamic');
