INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392193, 'Ferrous fumarate', 'Magnesium oxide', 'Moderate', 'INTERVAL: The bioavailability of orally administered iron may be reduced by concomitant administration of antacids or other agents with acid-neutralizing effects. The exact mechanism is unknown but may involve reduced iron solubility due to increase in gastric pH and/or reduced absorption due to complexation or precipitation of the iron.', 'DDInter', 1767317475, 'To minimize the potential for interaction, it may be appropriate to administer oral iron preparations at least two hours apart from antacids or other agents with acid-neutralizing effects.', 'Absorption', 'To minimize the potential for interaction, it may be appropriate to administer oral iron preparations at least two hours apart from antacids or other agents with acid-neutralizing effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
