INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392199, 'Fludeoxyglucose (18F)', 'Picosulfuric acid', 'Moderate', 'Certain medications may interfere with the results of diagnostic studies using fludeoxyglucose F18 by increasing or decreasing uptake of the agent into tissues.', 'DDInter', 1767317475, 'Clinicians should be aware of possible diagnostic interference associated with the following medications: colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', 'Absorption', 'Clinicians should be aware of possible diagnostic interference associated with the following medications: colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392200, 'Fludrocortisone', 'Picosulfuric acid', 'Moderate', 'The overuse or abuse of laxatives can cause significant loss of electrolytes and potentiate the risk of hypokalemia associated with corticosteroid therapy. Corticosteroids promote the retention of sodium and water and the excretion of potassium. Although these effects are primarily associated with mineralocorticoids like fludrocortisone, they may also occur with higher dosages of glucocorticoids or adrenocorticotropic agents, particularly if given systemically for longer than brief periods.', 'DDInter', 1767317475, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant therapy with corticosteroids, particularly if fludrocortisone or large doses of a glucocorticoid or adrenocorticotropic agent is given, patients should be counseled to recognize potential signs and symptoms of hypokalemia such as fatigue, myalgia, and muscle weakness. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Antagonism', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', NULL, NULL, 'Moderate', 'pharmacodynamic');
