INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114004, 'Efalizumab', 'Trifluridine', 'Moderate', 'The use of efalizumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Efalizumab alone can cause serious infections requiring hospitalization, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated.', NULL, 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated. Patients receiving other immunosuppressive agents should not be treated with efalizumab because of the possibility of excessive immunosuppression. The duration of the period following treatment with efalizumab before one should consider starting other immunosuppressive therapy has not been evaluated.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114140/', '[1] "Product Information. Raptiva (efalizumab)." Genentech, South San Francisco, CA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114005, 'Encorafenib', 'Tipiracil', 'Moderate', 'Coadministration with encorafenib may theoretically increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter (OCT2), organic anion transporter (OAT1, OAT3), or organic anion transporting polypeptide (OATP1B1, OATP1B3). The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by encorafenib.', NULL, 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever encorafenib is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114141/', '[1] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc., Boulder, CO.[4] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767365206);
