INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114059, 'Trifluridine', 'Smallpox (Vaccinia) Vaccine, Live', 'Major', 'The administration of live smallpox virus vaccine during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued for at least 3 months in most cases. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114195/', '[1] "Product Information. Dryvax (smallpox vaccine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 