INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114385, 'Vilanterol', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the systemic exposure to vilanterol following oral inhalation, as it is primarily metabolized by the isoenzyme. The interaction has been studied with fluticasone-vilanterol and ketoconazole.', NULL, 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. An increased risk of systemic corticosteroid effects should also be considered when fluticasone-vilanterol is used.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114521/', '[1] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[2] Kempsford R, Norris V, Siederer S "Vilanterol trifenatate, a novel inhaled long-acting beta2 adrenoceptor agonist, is well tolerated in healthy subjects and demonstrates prolonged bronchodilation in subjects with asthma and COPD." Pulm Pharmacol Ther 26 (2013): 256-64[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114386, 'Vilanterol', 'Atenolol', 'Moderate', 'Although cardioselective beta-blockers do not generally inhibit the bronchodilating effect of beta-2 adrenergic agonists, they may worsen pulmonary function in patients with asthma or other obstructive airway diseases. Cardioselective beta-blockers can occasionally precipitate acute bronchospasm in these patients, despite relative selectivity for beta-1 receptors in cardiac tissues. Presumably, beta-1 selectivity is dose-dependent and may be lost given a sufficient dose of the beta-blocker in susceptible patients. Other mechanisms may also be involved in bronchoconstriction that are unrelated to beta-2 blockade--for example, their effects on cholinergic M2 receptors and alpha-1 adrenoreceptors.', NULL, 'Beta-blockers, including those with relative cardioselectivity, should generally be avoided in patients with bronchospastic diseases.', NULL, 'Beta-blockers, including those with relative cardioselectivity, should generally be avoided in patients with bronchospastic diseases. However, given their demonstrated benefit in such conditions as heart failure, myocardial infarction, cardiac arrhythmias and hypertension, cardioselective beta-blockers may be administered with caution to those who do not respond to or tolerate alternative treatment. The benefits generally outweigh the risks in patients with mild or moderate reactive airway disease that is well controlled on inhaled corticosteroids and beta-2 agonists, provided they have no prior history suggesting a predisposition to severe exacerbations.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/