INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114493, 'Fluticasone', 'Vilanterol', 'Minor', 'Although they are often combined in clinical practice, the concomitant use of beta-2 adrenergic agonists and corticosteroids may result in additive hypokalemic effects. Since beta-2 agonists can sometimes cause QT interval prolongation, the development of hypokalemia may potentiate the risk of ventricular arrhythmias including torsade de pointes. However, clinical data are limited, and the potential significance is unknown. Patients who are receiving systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or systemic corticosteroid therapy may be at a greater risk of developing hypokalemia.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114629/', '[1] "Product Information. Foradil (formoterol)" Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114494, 'Formoterol', 'Vilanterol', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', NULL, 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', NULL, 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114630/', '[1] Lowe MD, Rowland E, Brown MJ, Grace AA "Beta(2) adrenergic receptors mediate important electrophysiological effects in human ventricular myocardium." Heart 86 (2001): 45-51[2] "Product Information. Brovana (arformoterol)." Sepracor Inc, Marlborough, MA.[3] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[4] "Product Information. Maxair (pirbuterol)." 3M Pharmaceuticals, St. Paul, MN.[5] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Proventil (albuterol)." Schering Laboratories, Kenilworth, NJ.[7] Wong CS, Pavord ID, Williams J, Britton JR, Tattersfield AE "Bronchodilator, cardiovascular, and hypokalaemic effects of fenoterol, salbutamol, and terbutaline in asthma." Lancet 336 (1990): 1396-9[8] "Product Information. Foradil (formoterol)" Novartis Pharmaceuticals, East Hanover, NJ.[9] "Product Information. Striverdi Respimat (olodaterol)." Boehringer Ingelheim, Ridgefield, CT.[10] "Product Information. Severent (salmeterol)." Glaxo Wellcome, Research Triangle Park, NC.[11] "Product Information. Xopenex (levalbuterol)." Sepracor, Marlborough, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114495, 'Fosamprenavir', 'Vilanterol', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the systemic exposure to vilanterol following oral inhalation, as it is primarily metabolized by the isoenzyme. The interaction has been studied with fluticasone-vilanterol and ketoconazole.', NULL, 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. An increased risk of systemic corticosteroid effects should also be considered when fluticasone-vilanterol is used.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114631/', '[1] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[2] Kempsford R, Norris V, Siederer S "Vilanterol trifenatate, a novel inhaled long-acting beta2 adrenoceptor agonist, is well tolerated in healthy subjects and demonstrates prolonged bronchodilation in subjects with asthma and COPD." Pulm Pharmacol Ther 26 (2013): 256-64[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
