        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1392397, 'Benzylpenicillin', 'Picosulfuric acid', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate. The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis. The clinical significance of this effect remains unknown.', NULL, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', NULL, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic. If concomitant use is required, additional bowel cleansing and colonoscopy may be required.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
