INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114568, 'Vilanterol', 'Lonafarnib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the systemic exposure to vilanterol following oral inhalation, as it is primarily metabolized by the isoenzyme. The interaction has been studied with fluticasone-vilanterol and ketoconazole.', NULL, 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. An increased risk of systemic corticosteroid effects should also be considered when fluticasone-vilanterol is used.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114704/', '[1] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[2] Kempsford R, Norris V, Siederer S "Vilanterol trifenatate, a novel inhaled long-acting beta2 adrenoceptor agonist, is well tolerated in healthy subjects and demonstrates prolonged bronchodilation in subjects with asthma and COPD." Pulm Pharmacol Ther 26 (2013): 256-64[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114569, 'Vilanterol', 'Macimorelin', 'Major', 'Macimorelin can cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of macimorelin with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of macimorelin with other drugs that can prolong the QT interval should generally be avoided. A sufficient washout period following discontinuation of these drugs is recommended prior to macimorelin administration. Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114705/', '[1] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114570, 'Vilanterol', 'Mannitol', 'Moderate', 'Coadministration with beta-2 adrenergic agonists may potentiate the hypokalemic effects of potassium-wasting diuretics. Beta-2 agonists can cause clinically significant but usually transient decreases in serum potassium concentrations. Since QT prolongation is a possible side effect of beta-2 agonists, exacerbation of hypokalemia may increase the risk of torsade de pointes and other serious arrhythmias. The interaction may be more likely with systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or concomitant theophylline or corticosteroid therapy.', NULL, 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics.', NULL, 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics. Serum potassium level and cardiovascular status should be monitored, especially if the beta-2 agonist is administered systemically or by nebulizer. Patients should be advised to notify their physician if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114706/', '[1] "Product Information. Proventil (albuterol)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[3] Lipworth BJ, McDevitt DG, Struthers AD "Prior treatment with diuretic augments the hypokalemic and electrocardiographic effects of