INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114633, 'Vilanterol', 'Pexidartinib', 'Moderate', 'Coadministration with pexidartinib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is increased clearance due to pexidartinib-mediated induction of CYP450 3A4 isoenzyme.', NULL, 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range.', NULL, 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range. The potential for diminished therapeutic effects should be considered when pexidartinib is prescribed in combination with drugs that are substrates of CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring of the CYP450 3A4 substrate drug should be considered whenever pexidartinib is added to or withdrawn from therapy with these drugs.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114769/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[3] "Product Information. Sonata (zaleplon)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767365206);
