INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114791, 'Vancomycin', 'Rapacuronium', 'Moderate', 'Like other polypeptide antibiotics, vancomycin may prolong apnea and respiratory paralysis after use of neuromuscular blocking agents. The mechanism may be related to decreased intracellular potassium or decreased ionized serum calcium.', NULL, 'Patients should be monitored for excessive and prolonged neuromuscular blockade.', NULL, 'Patients should be monitored for excessive and prolonged neuromuscular blockade.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114928/', '[1] Kronenfeld MA, Thomas SJ, Turndorf H "Recurrence of neuromuscular blockade after reversal of vecuronium in a patient receiving polymyxin/amikacin sternal irrigation." Anesthesiology 65 (1986): 93-4[2] Huang KC, Heise A, Shrader AK, Tsueda K "Vancomycin enhances the neuromuscular blockade of vecuronium." Anesth Analg 71 (1990): 194-6', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114792, 'Vancomycin', 'Remdesivir', 'Moderate', 'Coadministration of remdesivir with nephrotoxic agents may increase the plasma concentrations of its main metabolite, GS-441524, and the excipient, sulfobutylether-beta-cyclodextrin sodium (SBECD). Both GS-441524 and SBECD are primarily eliminated by the kidneys, thus renal impairment secondary to the use of nephrotoxic agents may reduce their clearance and increase the risk of accumulation.', NULL, 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function.', NULL, 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function. Caution is advised if remdesivir is used in patients who have recently received potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function (estimated glomerular filtration rate, or eGFR, for adult and pediatric patients older than 28 days; serum creatinine for full-term neonates at least 7 days up to 28 days old) should be evaluated prior to starting remdesivir and monitored daily during treatment or as clinically appropriate.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114929/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[5] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114793, 'Human Rho(D) immune globulin', 'Vancomycin', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114930/', '[1] MMWR Morb Mortal Wkly Rep "Renal insufficiency and failure associated with immune globulin intravenous therapy -- United States, 1985-1998. Available from: URL: http://www.cdc.gov/mmwr/preview/mmwrhtml/mm4824a3.htm." ([1999 Jun 25]):[2] "Product Information. BabyBIG (botulism immune globulin)." FFF Enterprises, Temecula, CA.[3] Guo X, Nzerue C "How to prevent, recognize, and treat drug-induced nephrotoxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2002 Apr]):[4] Moses S "Nephrotoxic drugs, drug-induced nephrotoxicity. Available from: URL: http://www.fpnotebook.com/Renal/Pharm/NphrtxcDrgs.htm." ([2011 Nov 25]):[5] Perazella MA "Renal vulnerability to drug toxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2009]):[6] Levy JB, Pusey EY "Nephrotoxicity of intravenous immunoglobulin. Available from: URL: http://qjmed.oxfordjournals.org/content/93/11/751.full.pdf+html." ([2000]):[7] "Product Information. Respigam (respiratory syncytial virus immune globulin)." Medimmune Inc, Gaithersburg, MD.[8] "Product Information. Bivigam (immune globulin intravenous)." Biotest Pharmaceuticals Corporation, Boca Raton, FL.[9] "Product Information. Cytogam (cytomegalovirus immune globulin)." CSL Behring, King of Prussia, PA.[10] Naughton CA "Drug-induced nephrotoxicity. Available from: URL: http://www.aafp.org/afp/2008/0915/p743.html." ([2008]):[11] KDIGO. Kidney Disease Improving Global Outcomes "KDIGO clinical practice guideline for acute kidney injury. Available from: URL: http://www.kdigo.org/clinical_practice_guidelines/pdf/KDIGO-AKI-Suppl-Appendices-A-F_March2012.pdf." ([2012 Mar]):', 1767365206);
