INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114847, 'Vedolizumab', 'Sipuleucel-T', 'Moderate', 'Neither the leukapheresis procedure nor sipuleucel-T have been studied for use in combination with chemotherapy or immunosuppressive agents (e.g., systemic corticosteroids). Since sipuleucel-T is designed to stimulate the immune system, concurrent use of myelo- or immunosuppressive agents may alter the efficacy and/or safety of sipuleucel-T.', NULL, 'Patients should be carefully evaluated to determine whether it is medically appropriate to reduce or discontinue chemotherapy or immunosuppressive agents prior to treatment with sipuleucel-T.', NULL, 'Patients should be carefully evaluated to determine whether it is medically appropriate to reduce or discontinue chemotherapy or immunosuppressive agents prior to treatment with sipuleucel-T.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114984/', '[1] "Product Information. Provenge (sipuleucel-T)." Dendreon Corporation, Seattle, WA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114848, 'Vedolizumab', 'Smallpox (Vaccinia) Vaccine, Live', 'Major', 'The administration of live smallpox virus vaccine during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued for at least 3 months in most cases. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114985/', '[1] "Product Information. Dryvax (smallpox vaccine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] CDC. Centers for Disease Control and Prevention "Smallpox vaccination clinic guide. Logistical considerations and guidance for state and local planning for emergency, large-scale, voluntary administration of smallpox vaccine in response to a smallpox outbreak. Available from: URL: http://www.bt.cdc.gov" ([2002 Oct 16]):', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114849, 'Vedolizumab', 'Talimogene laherparepvec', 'Major', 'Talimogene laherparepvec is a live, attenuated herpes simplex virus. Administration during immunosuppressant or intense antineoplastic therapy may be associated with a risk of potentially life-threatening disseminated herpetic infection due to enhanced virus replication in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Treatment with talimogene laherparepvec may need to be deferred until after such therapy is discontinued and the patient''s immune system has sufficiently recovered.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114986/', '[1] "Product Information. Imlygic (talimogene laherparepvec)." Amgen USA, Thousand Oaks, CA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114850, 'Vedolizumab', 'Teriflunomide', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. Serious infections including sepsis, as well as opportunistic infections like Pneumocystis jiroveci pneumonia, pulmonary and extrapulmonary tuberculosis, and aspergillosis have been reported with the use of leflunomide, particularly in patients on concomitant hematotoxic therapy. Agents that may be significantly immuno- or myelosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids. Rare cases of pancytopenia, agranulocytosis, and thrombocytopenia have also occurred with leflunomide alone, but were most frequent in the presence of concomitant or recent use of methotrexate or other myelotoxic agents. Due to the prolonged elimination half-life of leflunomide''s active metabolite, an interaction may occur even when hematotoxic agents are initiated after the discontinuation of leflunomide. Administering a washout procedure with cholestyramine or activated charcoal helps to accelerate elimination of the active metabolite from plasma and reduce the overlap of systemic exposure to these agents.', NULL, 'Close monitoring for the development of infection is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immuno- or myelosuppressive agents, and vice versa.', NULL, 'Close monitoring for the development of infection is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immuno- or myelosuppressive agents, and vice versa. Platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. If evidence of serious infection or bone marrow suppression occurs, treatment should be stopped, and cholestyramine or charcoal administered to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114987/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation, Cambridge, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114851, 'Vedolizumab', 'Tofacitinib', 'Major', 'Coadministration of tofacitinib with other immunosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. Serious and sometimes fatal infections due to bacterial, mycobacterial, invasive fungal, viral, or other opportunistic pathogens have been reported in patients receiving tofacitinib, most of whom were taking concomitant immunosuppressants such as methotrexate or corticosteroids.', NULL, 'Tofacitinib should not be used in combination with other Janus kinase inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.', NULL, 'Tofacitinib should not be used in combination with other Janus kinase inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.g., azathioprine, cyclosporine). Patients receiving tofacitinib should be closely monitored for the development of signs and symptoms of infection during and after treatment, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. If a serious infection, an opportunistic infection, or sepsis develops, tofacitinib should be interrupted until the infection is controlled.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114988/', '[1] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114852, 'Vedolizumab', 'Trichophyton mentagrophytes', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114989/', '[1] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc, Wallingford, CT.[2] "Product Information. Tuberculin PPD (Mantoux) (tuberculin purified protein derivative)." Connaught Laboratories Ltd, Toronto, ON.[3] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc, San Diego, CA.[4] "Product Information. MSTA Mumps Skin Test Antigen (mumps skin test antigen)." Aventis Pharmaceuticals, Swiftwater, PA.[5] "Product Information. Multitest CMI (skin test antigens, multiple)." Aventis Pharmaceuticals, Swiftwater, PA.[6] "Product Information. Histolyn-Cyl (histoplasmin)." ALK Laboratories Inc, Wallingford, CT.', 1767365206);
