INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114902, 'Vitamin A', 'Tretinoin', 'Major', 'Concomitant use of a retinoid with vitamin A or other retinoids may result in hypervitaminosis A and additive toxicities. Patients may be at increased risk for potentially serious adverse effects such as vision impairment, pseudotumor cerebri (benign intracranial hypertension), mucositis, esophagitis, hepatitis, hypertriglyceridemia, pancreatitis, ileitis, and colitis.', NULL, 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance.', NULL, 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance. Patients should be made aware of vitamin A sources in vitamin supplements, fortified foods and over-the-counter medications, and be advised to contact their physician if they experience signs of hypervitaminosis A such as inflammation or bleeding of the gums or lips; dry, scaly skin; alopecia; pruritus; erythema; headache; vertigo; and nausea. Patients should discontinue the medication and seek immediate medical attention if they develop signs and symptoms of pseudotumour cerebri or hepatotoxicity.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115039/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Accutane (isotretinoin)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Soriatane (acitretin)." Roche Laboratories, Nutley, NJ.[5] "Product Information. Vesanoid (tretinoin)." Roche Laboratories, Nutley, NJ.', 1767365206);
