INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114976, 'Zinc acetate', 'Oxytetracycline', 'Moderate', 'INTERVAL: The bioavailability of oral tetracyclines may be significantly decreased during concurrent administration with zinc-containing products. Therapeutic failure may result. The proposed mechanism is chelation of tetracyclines by the zinc cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Patients receiving an oral tetracycline in combination with zinc-containing products should be advised to separate the times of administration by at least three to four hours.', NULL, 'Patients receiving an oral tetracycline in combination with zinc-containing products should be advised to separate the times of administration by at least three to four hours. Doxycycline may be an appropriate alternative, since its absorption may not be significantly affected by zinc coadministration.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115113/', '[1] Neuvonen PJ "Interactions with the absorption of tetracyclines." Drugs 11 (1976): 45-54[2] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[3] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[4] Gothoni G, Neuvonen PJ, Mattila M, Hackman R "Iron-tetracycline interaction: effect of time interval between the drugs." Acta Med Scand 191 (1972): 409-11[5] Penttila O, Hurme H, Neuvonen PJ "Effect of zinc sulphate on the absorption of tetracycline and doxycycline in man." Eur J Clin Pharmacol 9 (1975): 131-4[6] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[7] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114977, 'Zinc acetate', 'Penicillamine', 'Moderate', 'INTERVAL: Oral administration of aluminum, copper, iron, zinc, magnesium, and possibly other minerals such as calcium may decrease the gastrointestinal absorption of penicillamine, and vice versa. The proposed mechanism involves chelation of penicillamine to polyvalent cations, which leads to formation of a nonabsorbable complex. When used concomitantly with copper-chelating agents for the treatment of Wilson''s disease, zinc salts can compete with copper for chelation and renal excretion. The clinical significance is unknown, although reduced efficacy of one or both agents may theoretically occur. Zinc alone can reduce copper levels by blocking its absorption in the intestine and promoting fecal copper excretion.', NULL, 'Mineral supplements or other products containing polyvalent cations should be administered at least two hours before or two hours after the penicillamine dose if possible.', NULL, 'Mineral supplements or other products containing polyvalent cations should be administered at least two hours before or two hours after the penicillamine dose if possible.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115114/', '[1] "Product Information. Galzin (zinc acetate)." Teva Pharmaceuticals USA, North Wales, PA.[2] Anderson LA. Hakojarvi SL, Boudreaux SK "Zinc acetate treatment in Wilson''s disease." Ann Pharmacother 32 (1998): 78-87[3] Farinati F, Cardin R, Mestriner C, Sturniolo GC, Naccarato R "Mechanisms of pennicillamine and zinc in the treatment of Wilson''s disease." Am J Gastroenterol 90 (1995): 2264-5[4] Brewer GJ "Practical recommendations and new therapies for Wilson''s disease." Drugs 50 (1995): 240-9[5] Brewer GJ, Yuzbasiyan-Gurkan V, Johnson V, Dick RD, Wang Y "Treatment of Wilson''s disease with zinc: XI. Interaction with other anticopper agents." J Am Coll Nutr 12 (1993): 26-30[6] Osman MA, Patel RB, Schuna A, Sundstrom WR, Welling PG "Reduction in oral penicillamine absorption by food, antacid and ferrous sulfate." Clin Pharmacol Ther 33 (1983): 465-70[7] "Product Information. Cuprimine (penicillamine)." Merck & Co, Inc, West Point, PA.', 1767365206);
