INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (114998, 'Insulin human (inhalation, rapid acting)', 'Zanamivir', 'Moderate', 'INTERVAL: Bronchodilators and other orally inhaled products may alter the absorption of inhaled human insulin. In nondiabetic subjects with mild asthma (n=36) and moderate asthma (n=31), administration of albuterol 30 minutes prior to inhaled human insulin resulted in a mean increase in insulin systemic exposure (AUC).', NULL, 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended.', NULL, 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended. Blood glucose should be monitored closely, and the insulin dose adjusted accordingly.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115135/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[3] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[4] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[5] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes Care 25(Suppl 1) (2002): S50-S60[6] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[7] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a