INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392737, 'Polyethylene glycol (3350 with electrolytes)', 'Nilotinib', 'Major', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval.', 'DDInter', 1767317475, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Monitoring of baseline and posttreatment serum electrolyte levels is recommended, particularly in the elderly. Patients should be instructed to drink plenty of clear liquids before, during, and after the bowel preparation process.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
