INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392868, 'Clindamycin (topical)', 'Atracurium', 'Moderate', 'Lincosamides (e.g., clindamycin, lincomycin) have demonstrated neuromuscular blocking activity in vitro and may augment the pharmacologic effects of neuromuscular blocking agents. Clinically, the interaction has been reported in isolated cases involving atracurium, pancuronium, pipecuronium, succinylcholine, and tubocurarine.', 'DDInter', 1767317475, 'Patients receiving neuromuscular blockers in conjunction with lincosamide therapy should be monitored closely during and after anesthesia for development of respiratory depression, and life support should be readily available if needed. Anticholinesterases and calcium may not completely antagonize the neuromuscular blockade induced by lincosamides.', 'Synergism', 'Patients receiving neuromuscular blockers in conjunction with lincosamide therapy should be monitored closely during and after anesthesia for development of respiratory depression, and life support should be readily available if needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
