INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392904, 'Dolasetron', 'Clevidipine', 'Major', 'Dolasetron has been shown to cause dose-dependent prolongation of the PR and QRS intervals. There have been reports of second- and third-degree atrioventricular block, cardiac arrest, and serious ventricular arrhythmias including fatalities in both adult and pediatric patients.', 'DDInter', 1767317475, 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the PR or QRS interval, especially in the elderly and patients with known risk factors. An ECG should be obtained in these patients before initiating dolasetron and periodically during treatment. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat. Dolasetron should be avoided in patients with complete heart block or at risk for complete heart block, unless they have an implanted pacemaker.', 'Synergism', 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the PR or QRS interval, especially in the elderly and patients with known risk factors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392905, 'Donepezil', 'Clevidipine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 1767317475, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
