INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1392975, 'Clevidipine', 'Nefazodone', 'Moderate', 'Nefazodone may potentiate the hypotensive effect of many drugs including vasodilators and antihypertensive agents. Orthostatic hypotension may occur.', 'DDInter', 1767317475, 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive nefazodone with agents that can produce significant hypotension such as antihypertensive agents or vasodilators. Patients should be made aware of the possible side effects (e.g., dizziness, lightheadedness, orthostasis) and be cautioned about driving, operating machinery, or performing other hazardous tasks.', 'Synergism', 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive nefazodone with agents that can produce significant hypotension such as antihypertensive agents or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
