INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (115058, 'Inotersen', 'Kanamycin', 'Major', 'Coadministration of inotersen with other nephrotoxic agents may increase the risk of renal impairment due to additive adverse effects on the kidney. Inotersen can cause glomerulonephritis that may result in dialysis-dependent renal failure.', NULL, 'Caution is advised when inotersen is prescribed with nephrotoxic drugs and other drugs that may impair renal function (e.', NULL, 'Caution is advised when inotersen is prescribed with nephrotoxic drugs and other drugs that may impair renal function (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, cidofovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, cisplatin, clofarabine, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravascular contrast media; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) . Serum creatinine, estimated glomerular filtration rate (eGFR), urine protein to creatinine ratio (UPCR), and a urinalysis should be obtained prior to initiation of inotersen and regularly during and for at least 8 weeks after treatment in accordance with the product labeling. Inotersen should generally not be initiated in patients with a UPCR of 1000 mg/g or higher, or in patients who are unable to adhere to the recommended laboratory monitoring and management guidelines. Patients or their caregivers should be apprised of the signs and symptoms of glomerulonephritis and to seek medical attention if they occur, including edema, shortness of breath, coughing, hematuria, and decreased urination.', 'Excretion', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115195/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Tegsedi (inotersen)." Akcea Therapeutics, Cambridge, MA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (115059, 'Inotersen', 'Ketoprofen', 'Major', 'Coadministration of inotersen and drugs that interfere with platelet function such as nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of serious, potentially life-threatening bleeding complications, including spontaneous intracranial and intrapulmonary hemorrhage. Inotersen causes reductions in platelet count that may result in sudden and unpredictable thrombocytopenia. Coadministration of inotersen with other potentially nephrotoxic agents such as NSAIDs may increase the risk of renal impairment due to additive adverse effects on the kidney. Inotersen can cause glomerulonephritis that may result in dialysis-dependent renal failure.', NULL, 'Caution is advised when inotersen is prescribed with NSAIDs.', NULL, 'Caution is advised when inotersen is prescribed with NSAIDs. A platelet count should be obtained prior to initiation of inotersen and regularly during and for at least 8 weeks after treatment in accordance with the product labeling. Inotersen should not be administered in patients with a platelet count below 100 x 10^9/L or in patients who are unable to adhere to the recommended laboratory monitoring and management guidelines. Patients or their caregivers should be apprised of the signs and symptoms of thrombocytopenia and to seek medical attention if they occur, including any unusual or prolonged bleeding (e.g., petechiae, easy bruising, hematoma, subconjunctival bleeding, gingival bleeding, epistaxis, hemoptysis, irregular or heavier than normal menstrual bleeding, hematemesis, hematuria, hematochezia, melena), neck stiffness, or atypical severe headache. Caution is advised when inotersen is prescribed with high dosages and/or chronic use of NSAIDs. Serum creatinine, estimated glomerular filtration rate (eGFR), urine protein to creatinine ratio (UPCR), and a urinalysis should be obtained prior to initiation of inotersen and regularly during and for at least 8 weeks after treatment in accordance with the product labeling. Inotersen should generally not be initiated in patients with a UPCR of 1000 mg/g or higher, or in patients who are unable to adhere to the recommended laboratory monitoring and management guidelines. Patients or their caregivers should be apprised of the signs and symptoms of glomerulonephritis and to seek medical attention if they occur, including edema, shortness of breath, coughing, hematuria, and decreased urination.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115196/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Tegsedi (inotersen)." Akcea Therapeutics, Cambridge, MA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (115060, 'Inotersen', 'Leflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115197/', '[1] EMEA "EMEA public statement on leflunomide (ARAVA) - severe and serious hepatic reactions. Available from URL: http://www.eudra.org/emea.html." ([1999 Sept 2]):[2] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (115061, 'Inotersen', 'Lepirudin', 'Major', 'Coadministration of inotersen and drugs that interfere with platelet function or coagulation may potentiate the risk of serious, potentially life-threatening bleeding complications, including spontaneous intracranial and intrapulmonary hemorrhage. Inotersen causes reductions in platelet count that may result in sudden and unpredictable thrombocytopenia.', NULL, 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding.', NULL, 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding. A platelet count should be obtained prior to initiation of inotersen and regularly during and for at least 8 weeks after treatment in accordance with the product labeling. Inotersen should not be administered in patients with a platelet count below 100 x 10^9/L or in patients who are unable to adhere to the recommended laboratory monitoring and management guidelines. Patients or their caregivers should be apprised of the signs and symptoms of thrombocytopenia and to seek medical attention if they occur, including any unusual or prolonged bleeding (e.g., petechiae, easy bruising, hematoma, subconjunctival bleeding, gingival bleeding, epistaxis, hemoptysis, irregular or heavier than normal menstrual bleeding, hematemesis, hematuria, hematochezia, melena), neck stiffness, or atypical severe headache.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115198/', '[1] "Pr