INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1393301, 'Clobazam', 'Nefazodone', 'Moderate', 'Nefazodone, when administered with alprazolam or triazolam, has been reported to increase their half-life, area under the plasma concentration-time curve, and peak plasma concentrations. Increased sedation and altered psychomotor performance have resulted. The mechanism appears to be inhibition of the CYP450 3A4 enzyme by nefazodone. This interaction may occur with all benzodiazepines that undergo oxidative metabolism, although it has been reported only with alprazolam and triazolam.', 'DDInter', 1767317475, 'Patients receiving this combination should be monitored for excessive and prolonged sedation. It may be necessary to adjust benzodiazepine dosage when nefazodone is started or stopped. Lorazepam, oxazepam, and temazepam undergo conjugative metabolism and may be suitable alternatives.', 'Metabolism', 'Patients receiving this combination should be monitored for excessive and prolonged sedation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1393302, 'Clobazam', 'Nelfinavir', 'Moderate', 'Nelfinavir competitively inhibits the CYP450 3A4 isoenzyme and may interfere with the metabolism of some benzodiazepines.', 'DDInter', 1767317475, 'Caution is advised if benzodiazepines are administered with nelfinavir. Patients should be monitored for the possibility of potentially serious and/or life threatening events (e.g. prolonged sedation, respiratory depression) associated with the accumulation of these agents.', 'Metabolism', 'Caution is advised if benzodiazepines are administered with nelfinavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1393303, 'Clobazam', 'Nilotinib', 'Moderate', 'Based on in vitro inhibition data, coadministration with nilotinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter and/or CYP450 2D6, CYP450 3A4, or UGT1A1 isoenzymes. The mechanism is decreased clearance via these routes due to inhibition by nilotinib.', 'DDInter', 1767317475, 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nilotinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
