INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1393378, 'Clobazam', 'Ritonavir', 'Moderate', 'The coadministration of ritonavir and benzodiazepines that are substrates of the CYP450 3A4 enzymatic pathway may result in elevated plasma concentrations of the latter. The mechanism is decreased clearance of benzodiazepines due to inhibition of CYP450 3A4 activity by ritonavir.', 'DDInter', 1767317475, 'While clinical data are not available for all agents in the class, the possibility of prolonged and/or increased pharmacologic effects of benzodiazepines, including sedation and respiratory depression, should be considered in patients receiving ritonavir therapy. It may be appropriate to use lower benzodiazepine dosages in these patients, or use agents that are not metabolized by the CYP450 3A4 pathway (e.g., lorazepam, oxazepam, temazepam).', 'Metabolism', 'While clinical data are not available for all agents in the class, the possibility of prolonged and/or increased pharmacologic effects of benzodiazepines, including sedation and respiratory depression, should be considered in patients receiving ritonavir therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
