INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1393572, 'Denosumab', 'Clofarabine', 'Moderate', 'Concomitant use of immunosuppressive or myelosuppressive agents with denosumab may increase the risk of serious infections. Denosumab binds to and inhibits the receptor activator of nuclear factor kappa-B ligand (RANKL), which is expressed on activated T and B lymphocytes and in lymph nodes. Thus, denosumab alone may increase the risk of infections.', 'DDInter', 1767317475, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. The need for continued denosumab therapy should be assessed when serious infections occur during treatment.', 'Synergism', 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1393573, 'Diatrizoate', 'Clofarabine', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', 'DDInter', 1767317475, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, NULL, 'Major', 'pharmacodynamic');
