INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1393894, 'Dasatinib', 'Clofazimine', 'Moderate', 'Clofazimine may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Caution is recommended if clofazimine is used in combination with other drugs that can prolong the QT interval. Consider monitoring electrocardiograms, particularly in patients receiving clofazimine dosages greater than 100 mg/day, and discontinue QT-prolonging medications if clinically significant ventricular arrhythmia is noted or if QTcF interval >=500 ms is observed. If syncope occurs, obtain an ECG to detect QT prolongation.', 'Synergism', 'Caution is recommended if clofazimine is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
