INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1393899, 'Deutetrabenazine', 'Clofazimine', 'Moderate', 'Deutetrabenazine has been associated with modest QT interval prolongation. In addition, central nervous system (CNS)-depressant effects may be additively or synergistically increased in patients taking deutetrabenazine with certain other drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'Coadministration of deutetrabenazine with other drugs that can prolong the QT interval should generally be avoided. Deutetrabenazine is not recommended for use in patients with congenital long QT syndrome or with arrhythmias associated with a prolonged QT interval. If concomitant use is required, assessment of baseline QT interval and periodic monitoring during therapy may be considered. In addition, dosage reduction may be considered in patients on concurrent therapy with a strong CYP450 2D6 inhibitor, or in patients who are poor metabolizers of CYP450 2D6.', 'Metabolism, Synergism', 'Coadministration of deutetrabenazine with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1393900, 'Disopyramide', 'Clofazimine', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, NULL, 'Major', 'pharmacodynamic');
