INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394113, 'Dabrafenib', 'Clomipramine', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', 'DDInter', 1767317475, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394114, 'Dacomitinib', 'Clomipramine', 'Moderate', 'Coadministration with dacomitinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 2D6 by dacomitinib.', 'DDInter', 1767317475, 'Caution is advised if dacomitinib is used in combination with CYP450 2D6 substrates. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever dacomitinib is added to or withdrawn from therapy. Avoid concomitant use with dacomitinib where minimal increases in concentration of the CYP450 2D6 substrate may lead to serious or life-threatening toxicities.', 'Metabolism', 'Caution is advised if dacomitinib is used in combination with CYP450 2D6 substrates.', NULL, NULL, 'Moderate', 'pharmacodynamic');
