INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (117318, 'Interferon alfa-2b', 'Rasagiline', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of rasagiline, which is primarily metabolized by the isoenzyme.', NULL, 'In patients receiving CYP450 1A2 inhibitors, it may be appropriate to initiate therapy at 0.', NULL, 'In patients receiving CYP450 1A2 inhibitors, it may be appropriate to initiate therapy at 0.5 mg/day, then increase gradually as needed and as tolerated. Patients should be advised to promptly seek medical attention if they experience potential signs and symptoms of a hypertensive crisis such as severe headache, visual disturbances, confusion, stupor or coma, seizures, chest pain, unexplained nausea or vomiting, and stroke-like symptoms.', 'Metabolism', 'Interferon alfa-n1, Oprelvekin, Sipuleucel-T, Elapegademase, Interferon beta-1b, Ropeginterferon alfa-2b, Interferon alfacon-1, Peginterferon beta-1a, Interferon beta-1a, Sargramostim, Histamine, More', 'Apomorphine, Opicapone, Cabergoline, Amantadine, Entacapone, Selegiline, Tolcapone, Rotigotine, Bromocriptine, Pergolide, Pramipexole, Safinamide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/117455/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Azilect (rasagiline)." Teva Pharmaceuticals USA, North Wales, PA.[4] McGrath PJ, Stewart JW, Quitkin FM "A possible L-deprenyl induced hypertensive reaction." J Clin Psychopharmacol 9 (1989): 310-1[5] "Product Information. Azilect (rasagiline)." Teva Pharmaceuticals USA, North Wales, PA.[6] Swee