INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (117910, 'Paliperidone', 'Levocetirizine', 'Moderate', 'Concurrent use of cetirizine or levocetirizine with alcohol or other agents that exhibit central nervous system (CNS) depressant effects may result in additive impairment of mental alertness and performance.', NULL, 'Concomitant use of cetirizine or levocetirizine with alcohol or other CNS depressants should generally be avoided if possible.', NULL, 'Concomitant use of cetirizine or levocetirizine with alcohol or other CNS depressants should generally be avoided if possible. In the event that they are used together, patients should be counseled against driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118048/', '[1] "Product Information. Zyrtec (cetirizine)." Pfizer US Pharmaceuticals, New York, NY.[2] "Product Information. Xyzal (levocetirizine)." UCB Pharma Inc, Smyrna, GA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15