INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394243, 'Histamine', 'Clomipramine', 'Moderate', 'Tricyclic and some atypical antidepressants have antihistaminic properties and may decrease skin reactivity to histamine phosphate for up to several weeks. Mirtazapine, doxepin, trimipramine and amitriptyline have the strongest affinity for H1 receptors.', 'DDInter', 1767317475, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering histamine phosphate to patients treated with antidepressants.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering histamine phosphate to patients treated with antidepressants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
