INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394286, 'Clomipramine', 'Labetalol', 'Moderate', 'Limited data suggest that labetalol can increase the plasma concentrations of tricyclic antidepressants. The proposed mechanism is competition for the same metabolic pathway. The manufacturers of labetalol indicate that concomitant use with tricyclic antidepressants may increase the incidence of tremors and hypotensive events. Carvedilol may also interact similarly.', 'DDInter', 1767317475, 'Close monitoring for clinical response and tolerance is recommended whenever labetalol is added to or discontinued from an antidepressant regimen. Patients should be advised to notify their physician if they experience excessive antidepressant effects such as tremor, dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or hypotension. Dose adjustments may be necessary.', 'Metabolism', 'Close monitoring for clinical response and tolerance is recommended whenever labetalol is added to or discontinued from an antidepressant regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394287, 'Clomipramine', 'Lamotrigine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
