INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394446, 'Clomipramine', 'Primaquine', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317475, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394447, 'Clomipramine', 'Primidone', 'Moderate', 'Tricyclic antidepressants may counteract the anticonvulsive effects of barbiturates by lowering the seizure threshold. Barbiturates may decrease the serum levels and effects of tricyclic antidepressants by induction of hepatic metabolism.', 'DDInter', 1767317475, 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control. Dose adjustments may be required. Monitoring for clinical evidence of additive toxicity and for clinical and/or laboratory evidence of reduced antidepressant effect is also advisable.', 'Antagonism', 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control.', NULL, NULL, 'Moderate', 'pharmacodynamic');
