INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394465, 'Clomipramine', 'Quinestrol', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 1767317475, 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The clinical significance of this interaction has not been established.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394466, 'Clomipramine', 'Quinidine', 'Moderate', 'Quinidine may markedly increase the serum levels of some tricyclic antidepressants (TCAs). The mechanism is related to inhibition of CYP450 2D6 metabolism. Extensive metabolizers of debrisoquine may be at greater risk of developing significant TCA toxicity. Desipramine, nortriptyline, and imipramine have been specifically implicated in this interaction. In addition, both quinidine and TCAs may cause prolongation of the QT interval which could result in torsades de pointes.', 'DDInter', 1767317475, 'If possible, this combination should be avoided. Close clinical, electrocardiographic, and laboratory monitoring is recommended for patients who do receive these drugs concurrently. Patients should be advised to promptly notify their physicians if they experience irregular heartbeats or palpitations, syncope, drowsiness, confusion, weakness, or seizures.', 'Metabolism', 'If possible, this combination should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394467, 'Clomipramine', 'Quinine', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317475, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1394468, 'Clomipramine', 'Racepinephrine', 'Moderate', 'Concomitant use of beta-2 adrenergic agonists with tricyclic antidepressants (TCAs) may increase the risk and/or severity of cardiovascular adverse effects such as hypertension, palpitation, chest pain, and arrhythmia.', 'DDInter', 1767317475, 'Caution is advised if beta-2 agonists are used with TCAs, or within two weeks of discontinuing the latter. Cardiovascular status should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 agonists are used with TCAs, or within two weeks of discontinuing the latter.', NULL, NULL, 'Moderate', 'pharmacodynamic');
