    ) VALUES (1394794, 'Clonazepam', 'Nelfinavir', 'Moderate', 'Nelfinavir competitively inhibits the CYP450 3A4 isoenzyme and may interfere with the metabolism of some benzodiazepines.', NULL, 'Caution is advised if benzodiazepines are administered with nelfinavir.', NULL, 'Caution is advised if benzodiazepines are administered with nelfinavir. Patients should be monitored for the possibility of potentially serious and/or life threatening events (e.g. prolonged sedation, respiratory depression) associated with the accumulation of these agents.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
