INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (118503, 'Venetoclax', 'Saquinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of venetoclax, which is primarily metabolized by the isoenzyme. Additionally, inhibition of P-glycoprotein (P-gp) and breast cancer resistance protein (BCRP) efflux transporters by ketoconazole may contribute. Increased venetoclax exposure may potentiate the risk of tumor lysis syndrome, particularly at initiation of therapy and during the dosage ramp-up phase, as well as other adverse effects such as diarrhea, nausea, vomiting, neutropenia, anemia, and thrombocytopenia.', NULL, 'Concomitant use of potent CYP450 3A4 inhibitors at initiation and during the dosage ramp-up phase of venetoclax is considered contraindicated.', NULL, 'Concomitant use of potent CYP450 3A4 inhibitors at initiation and during the dosage ramp-up phase of venetoclax is considered contraindicated. If a potent CYP450 3A4 inhibitor is required after the ramp-up phase, the manufacturer recommends that venetoclax dosage be reduced by at least 75%. The dosage used prior to initiating the CYP450 3A4 inhibitor may be resumed 2 to 3 days after discontinuation of the inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118641/', '[1] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (200