INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (118518, 'Zolpidem', 'Saquinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zolpidem, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when zolpidem is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when zolpidem is used with CYP450 3A4 inhibitors. Patients should be advised to avoid driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until they know how the medications affect them, and to notify their physician if they experience excessive somnolence or dizziness.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118656/', '[1] Luurila H, Kivisto KT, Neuvonen PJ "Effect of itraconazole on the pharmacokinetics and pharmacodynamics of zolpidem." Eur J Clin Pharmacol 54 (1998): 163-6[2] Greenblatt DJ, vonMoltke LL, Harmatz JS, Mertzanis P, Graf JA, Durol ALB, Counihan M, RothSchechter B, Shader RI "Kinetic and dynamic interaction study of zolpidem with ketoconazole, itraconazole, and fluconazole." Clin Pharmacol Ther 64 (1998): 661-71[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Greenblatt DJ, von Moltke LL, Harmatz JS, et al. "Differential impairment of triazolam and zolpidem clearance by ritonavir." J Acquir Immune Defic Syndr 24 (2000): 129-36[5] Saari TI, Laine K, Leino K, Valtonen M, Neuvonen PJ, Olkkola KT "Effect of voriconazole on the pharmacokinetics and pharmacodynamics of zolpidem in healthy subjects." Br J Clin Pharmacol 63 (2007): 116-20[6] Farkas D, Volak L, Harmat