INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (118739, 'Iopromide', 'Ibutilide', 'Moderate', 'The use of iodine-containing contrast media for coronary angiography in patients treated with certain antiarrhythmics such as amiodarone may result in significant prolongation of the QT interval. These contrast agents can be arrhythmogenic when injected into the coronary arteries and may have additive effects on cardiac repolarization when coadministered with antiarrhythmic agents that prolong the QT interval.', NULL, 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.', NULL, 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.g., disopyramide, quinidine, procainamide) or class III (e.g., amiodarone, dofetilide, ibutilide, sotalol) antiarrhythmic agents. Increased surveillance and ECG monitoring may be appropriate. Patients who receive outpatient angiographies should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of arrhythmia such as dizziness, palpitations, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118877/', '[1] Goernig M, Kirmeier T, Krack A, Hartog CS, Figulla HR, Leder U "Iohexol contrast medium induces QT prolongation in amiodarone patients." Br J Clin Pharmacol 58 (2004): 96-98[2] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[3] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (118740, 'Iopromide', 'Ifosfamide', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118878/', '[1] Dubrovskaya Y, Prasad N, Lee Y, Esaian D, Figueroa DA, Tam VH "Risk factors for nephrotoxicity onset associated with polymyxin B therapy." J Antimicrob Chemother 70 (2015): 1903-7[2] Bentley ML, Corwin HL, Dasta J "Drug-induced acute kidney injury in the critically ill adult: recognition and prevention strategies." Crit Care Med 38(6 Suppl) (2010): S169-74[3] Kellum JA, Leblanc M, Venkataraman R "Acute renal failure." BMJ Clin Evid 9 (2008): 2001[4] van den Berk G, Tonino S, de Fijter C, Smit W, Schultz MJ "Bench-to-bedside review: Preventative measures for contrast-induced nephropathy in critically ill patients." Crit Care 9 (200