INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (118774, 'Iopromide', 'Polythiazide', 'Moderate', 'Forced diuresis during administration of radiocontrast agents may increase the risk of renal impairment in patients who are at high risk for contrast-induced nephropathy.', NULL, 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy.', NULL, 'Whenever possible, alternative imaging techniques should be considered in patients who are at high risk for contrast-induced nephropathy. Otherwise, some experts recommend discontinuing diuretics 1 to 2 days before administration of contrast media, depending on the clinical feasibility of doing so. The smallest effective dose of a nonionic, hypo- or iso-osmolar contrast medium (e.g., iohexol, iodixanol, iopamidol) should be used, since the risk of nephropathy is increased with increasing contrast dose and/or osmolarity. Repeat procedures with contrast media, if necessary, should not occur until at least 72 hours after the previous contrast exposure and renal function has fully recovered. Although it is not necessary to measure the serum creatinine levels of every patient before contrast administration, measurements should generally be made in patients receiving contrast agent by intraarterial administration (which is associated with increased risk of nephropathy relative to intravenous administration) and patients with a history of kidney disease, proteinuria, kidney surgery, diabetes, hypertension, gout, or other risk factors for nephropathy. Creatinine measurements should be continued for 24 to 48 hours after administration of contrast medium. It is important that patients be adequately hydrated with either saline or sodium bicarbonate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118912/', '[1] Briguori C, Marenzi G "Contrast-induced nephropathy: Pharmacological prophylaxis." Kidney Int 69(S100) (2006): S30-S38[2] Venkataraman R "Can we prevent acute kidney injury?" Crit Care Med 36(4 Suppl) (2008): S166-71[3] Barrett BJ, Parfrey PS "Clinical practice. Preventing nephropathy induced by contrast medium." N Engl J Med 354 (2006): 379-86[4] Tepel M, Aspelin P, Lameire N "Contrast-induced nephropathy: a clinical and evidence-based approach." Circulation 113 (2006): 1799-806[5] "Product Information. Lasix (furosemide)." sanofi-aventis , Bridgewater, NJ.[6] Kelly AM, Dwamena B, Cronin P, Bernstein SJ, Carlos RC "Meta-analysis: effectiveness of drugs for preventing contrast-induced nephropathy." Ann Intern Med 148 (2008): 284-94[7] Weinstein J-M, Heyman S, Brezis M "Potential deleterious effect of furosemide in radiocontrast nephropathy." Nephron 62 (1992): 413-5[8] Massicotte A "Contrast medium-induced nephropathy: strategies for prevention." Pharmacotherapy 28 (2008): 1140-50[9] Fishman EK, Reddan D "What are radiologists doing to prevent contrast-induced nephropathy (CIN) compared with measures supported by current evidence? A survey of European radiologists on CIN associated with computed tomography." Acta Radiol 49 (2008): 310-20[10] Stacul F, Adam A, Becker CR, et al. "Strategies to reduce the risk of contrast-induced nephropathy." Am J Cardiol 98(6S1) (2006): 59-77[11] Meschi M, Detrenis S, Musini S, Strada E, Savazzi G "Facts and fallacies concerning the prevention of contrast medium-induced nephropathy." Crit Care Med 34 (2006): 2060-80[12] Solomon R, Werner C, Mann D, D''Elia J, Silva P "Effects of saline, mannitol, and furosemide on acute decreases in renal function induced by radiocontrast agents." N Engl J Med 331 (1994): 1416-20[13] Stevens MA, McCullough PA, Tobin KJ, et al. "A prospective randomized trial of prevention measures in patients at high risk for contrast nephropathy: results of the P.R.I.N.C.E. Study. Prevention of Radiocontrast Induced Nephropathy Clinical Evaluation." J Am Coll Cardiol 33 (1999): 403-11[14] Ho KM, Sheridan DJ "Meta-analysis of frusemide to prevent or treat acute renal failure." BMJ 333 (2006): 420[15] Costa N "Understanding contrast media." J Infus Nurs 27 (2004): 302-12', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (118775, 'Iopromide', 'Procainamide', 'Moderate', 'The use of iodine-containing contrast media for coronary angiography in patients treated with certain antiarrhythmics such as amiodarone may result in significant prolongation of the QT interval. These contrast agents can be arrhythmogenic when injected into the coronary arteries and may have additive effects on cardiac repolarization when coadministered with antiarrhythmic agents that prolong the QT interval.', NULL, 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.', NULL, 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.g., disopyramide, quinidine, procainamide) or class III (e.g., amiodarone, dofetilide, ibutilide, sotalol) antiarrhythmic agents. Increased surveillance and ECG monitoring may be appropriate. Patients who receive outpatient angiographies should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of arrhythmia such as dizziness, palpitations, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118913/', '[1] Goernig M, Kirmeier T, Krack A, Hartog CS, Figulla HR, Leder U "Iohexol contrast medium induces QT prolongation in amiodarone patients." Br J Clin Pharmacol 58 (2004): 96-98[2] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[3] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (118776, 'Iopromide', 'Propranolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118914/', '[1] "Product Information. Osmovist 240 (iotrolan)." Berlex Canada Inc, Lachine, QC.[2] Costa N "Understanding contrast media." J Infus Nurs 27 (2004): 302-12[3] "Product Information. Visipaque (iodixanol)." Nycomed Inc, Princeton, NJ.[4] "Product Information. Ultravist (iopromide)." Berlex, Richmond, CA.', 1767363024);
