INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395152, 'Clonidine', 'Repaglinide', 'Moderate', 'Clonidine may diminish the physiological response to hypoglycemia induced by insulin and/or oral hypoglycemic agents (i.e., insulin secretagogues such as sulfonylureas and meglitinides). Clonidine inhibits the release of catecholamines, which are responsible for recovery of normal blood glucose levels and symptoms of hypoglycemia such as tremor, sweating, tachycardia, and blood pressure changes.', 'DDInter', 1767317475, 'Patients should be instructed about the need for regular monitoring of blood glucose levels and be aware that certain symptoms of hypoglycemia such as tremors, sweating, and tachycardia may be reduced or absent during treatment with clonidine.', 'Synergism', 'Patients should be instructed about the need for regular monitoring of blood glucose levels and be aware that certain symptoms of hypoglycemia such as tremors, sweating, and tachycardia may be reduced or absent during treatment with clonidine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
