INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395208, 'Clonidine', 'Vardenafil', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', 'DDInter', 1767317475, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395209, 'Clonidine', 'Venlafaxine', 'Moderate', 'The antihypertensive effects of clonidine may be antagonized by serotonin-norepinephrine reuptake inhibitors. The proposed mechanism is opposing effects on adrenergic alpha-2 receptors.', 'DDInter', 1767317475, 'If concomitant use cannot be avoided, monitoring for altered efficacy of clonidine is recommended. In some cases, the dosage of clonidine may require upward adjustment.', 'Antagonism', 'If concomitant use cannot be avoided, monitoring for altered efficacy of clonidine is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395210, 'Clonidine', 'Verapamil', 'Moderate', 'Coadministration of clonidine with agents known to affect sinus node function of AV nodal conduction may be associated with significantly reduced heart rate. The exact mechanism of interaction is unknown. However, these drugs individually have been associated with AV node dysfunction and may have additive effects during coadministration.', 'DDInter', 1767317475, 'Heart rate and blood pressure should be closely monitored in patients receiving clonidine in combination with agents known to affect sinus node function of AV nodal conduction such as digitalis and certain calcium channel blockers.', 'Synergism', 'Heart rate and blood pressure should be closely monitored in patients receiving clonidine in combination with agents known to affect sinus node function of AV nodal conduction such as digitalis and certain calcium channel blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395211, 'Clonidine', 'Verteporfin', 'Moderate', 'Drugs that decrease clotting, vasoconstriction, or platelet aggregation may interfere with the therapeutic effect of verteporfin. These agents may antagonize the physiologic responses to local damage of neovascular endothelium induced by activated verteporfin, which include the release of procoagulant and vasoactive factors that result in vessel occlusion. However, the clinical significance is unknown.', 'DDInter', 1767317475, 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy. Otherwise, the potential for diminished efficacy of verteporfin should be considered.', 'Antagonism', 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
