INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395263, 'Fluconazole', 'Clopidogrel', 'Major', 'Coadministration with potent inhibitors of CYP450 2C19 may reduce the efficacy of clopidogrel, whose antiplatelet effect is dependent in part on bioactivation by the isoenzyme to a pharmacologically active metabolite.', 'DDInter', 1767317475, 'Based on existing data, patients treated with clopidogrel should preferably avoid the concomitant use of potent CYP450 2C19 inhibitors such as fluconazole, fluoxetine, fluvoxamine, and ticlopidine. Concomitant use of selective serotonin reuptake inhibitors or ticlopidine with clopidogrel may also potentiate the risk of bleeding complications due to additive or synergistic effects on the clotting cascade.', 'Metabolism', 'Based on existing data, patients treated with clopidogrel should preferably avoid the concomitant use of potent CYP450 2C19 inhibitors such as fluconazole, fluoxetine, fluvoxamine, and ticlopidine.', NULL, NULL, 'Major', 'pharmacodynamic');
