INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395291, 'Clopidogrel', 'Levacetylmethadol', 'Moderate', 'Coadministration with opioid agonists may delay and reduce the absorption of orally administered P2Y12 inhibitors (e.g., clopidogrel, prasugrel, ticagrelor). The proposed mechanism may involve opioid-mediated slowed gastric emptying.', 'DDInter', 1767317475, 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists. In acute coronary syndrome patients who require an opioid agonist, the use of a parenteral antiplatelet agent, such as cangrelor, should be considered.', 'Absorption', 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395292, 'Clopidogrel', 'Levomilnacipran', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 1767317475, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
