INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395306, 'Clopidogrel', 'Nepafenac (ophthalmic)', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', 'DDInter', 1767317475, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395307, 'Clopidogrel', 'Nintedanib', 'Moderate', 'Coadministration of nintedanib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Vascular endothelial growth factor receptor (VEGFR) inhibitors like nintedanib can cause bleeding.', 'DDInter', 1767317475, 'Concomitant use of nintedanib and medications that interfere with platelet function or coagulation should be approached cautiously. Close clinical and laboratory observation for bleeding complications is recommended for patients on full anticoagulation therapy, and the anticoagulant dose adjusted as necessary. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Concomitant use of nintedanib and medications that interfere with platelet function or coagulation should be approached cautiously.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395308, 'Clopidogrel', 'Niraparib', 'Moderate', 'Coadministration of niraparib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317475, 'Concomitant use of anticoagulants or other medications known to reduce platelet count should be approached with caution. For hematologic adverse reactions requiring transfusion, withhold niraparib and consider interrupting any drugs that interfere with platelet function or coagulation. any signs or symptoms of bleeding to their primary care provider.', 'Synergism', 'Concomitant use of anticoagulants or other medications known to reduce platelet count should be approached with caution.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395309, 'Clopidogrel', 'Oliceridine', 'Moderate', 'Coadministration with opioid agonists may delay and reduce the absorption of orally administered P2Y12 inhibitors (e.g., clopidogrel, prasugrel, ticagrelor). The proposed mechanism may involve opioid-mediated slowed gastric emptying.', 'DDInter', 1767317475, 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists. In acute coronary syndrome patients who require an opioid agonist, the use of a parenteral antiplatelet agent, such as cangrelor, should be considered.', 'Absorption', 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1395310, 'Clopidogrel', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of omacetaxine and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with omacetaxine is associated with a high frequency of thrombocytopenia.', 'DDInter', 1767317475, 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The CBC and platelet count should be monitored according to the manufacturer''s recommendations.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter.', NULL, NULL, 'Major', 'pharmacodynamic');
